(1, 1, 'Inspire Newsletter', 'inspirenewsletter', 'inspirethemes', '1.0.0', 'http://www.inspirethemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Inspire Newsletter</name>\n	<code>inspirenewsletter</code>\n	<version>1.0.0</version>\n	<author>inspirethemes</author>\n	<link>http://www.inspirethemes.com</link>\n</modification>', 1, '2018-02-25 12:37:08'),
(4, 3, 'd_opencart_patch', 'd_opencart_patch', 'Dreamvention', '3.0.9', 'http://dreamvention.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>d_opencart_patch</name>\n    <code>d_opencart_patch</code>\n    <description>Permission fixes in Opencart 3.x</description>\n    <version>3.0.9</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path=\"admin/controller/user/user_permission.php\">\n        <operation error=\"skip\">\n            <search><![CDATA[$path[] = $file . \'/*\';]]></search>\n            <add position=\"after\"><![CDATA[\n            //d_opencart_patch.xml 1\n            $files[] = $file;\n            ]]></add>\n        </operation>\n        <operation error=\"skip\">\n            <search><![CDATA[$permission = substr($controller, 0, strrpos($controller, \'.\'));]]></search>\n            <add position=\"replace\"><![CDATA[\n            //d_opencart_patch.xml 2\n            $permission = (strrpos($controller, \'.\') !== false) ? substr($controller, 0, strrpos($controller, \'.\')) : $controller;\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/controller/extension/installer.php\">\n        <operation error=\"skip\">\n            <search><![CDATA[$modification_info = $this->model_setting_modification->getModificationByCode($code);]]></search>\n            <add position=\"after\"><![CDATA[\n            //d_opencart_patch.xml 1\n            if (strpos($code, \'d_\') === 0 && $modification_info) {\n                $this->model_setting_modification->deleteModification($modification_info[\'modification_id\']);\n                $modification_info = false; \n            }\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/model/setting/extension.php\">\n        <operation error=\"skip\">\n            <search><![CDATA[$query = $this->db->query(\"SELECT * FROM `\" . DB_PREFIX . \"extension_path` WHERE `extension_install_id` = \'\" . (int)$extension_install_id . \"\' ORDER BY `date_added` ASC\");]]></search>\n            <add position=\"replace\"><![CDATA[\n        //d_opencart_patch.xml 1\n        $query = $this->db->query(\"SELECT * FROM `\" . DB_PREFIX . \"extension_install` WHERE `extension_install_id` = \'\" . (int)$extension_install_id . \"\'\");\n\n        $extension = $query->row;\n\n        if(isset($extension[\'filename\']) && strpos($extension[\'filename\'], \'_compiled_\')){\n\n\n            $parts = explode(\'_compiled_\', $extension[\'filename\']);\n            $extension_name = $parts[0];\n            $query = $this->db->query(\"SELECT * FROM `\" . DB_PREFIX . \"extension_path` WHERE `extension_install_id` = \'\" . (int)$extension_install_id . \"\' AND path LIKE \'%\".$this->db->escape($extension_name).\"%\' ORDER BY `date_added` ASC\");\n        }else{\n            $query = $this->db->query(\"SELECT * FROM `\" . DB_PREFIX . \"extension_path` WHERE `extension_install_id` = \'\" . (int)$extension_install_id . \"\' ORDER BY `date_added` ASC\");\n        }\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/model/setting/modification.php\">\n        <operation error=\"skip\">\n            <search><![CDATA[$this->db->query(\"DELETE FROM `\" . DB_PREFIX . \"modification` WHERE `extension_install_id` = \'\" . (int)$extension_install_id . \"\'\");]]></search>\n            <add position=\"replace\"><![CDATA[\n        //d_opencart_patch.xml 1\n        $query = $this->db->query(\"SELECT * FROM `\" . DB_PREFIX . \"extension_install` WHERE `extension_install_id` = \'\" . (int)$extension_install_id . \"\'\");\n\n        $extension = $query->row;\n\n        if(isset($extension[\'filename\']) && strpos($extension[\'filename\'], \'_compiled_\') !== false){\n\n\n            $parts = explode(\'_compiled_\', $extension[\'filename\']);\n            $extension_name = $parts[0];\n            $this->db->query(\"DELETE FROM `\" . DB_PREFIX . \"modification` WHERE `extension_install_id` = \'\" . (int)$extension_install_id . \"\' AND `name` LIKE \'%\".$this->db->escape($extension_name).\"%\'\");\n\n        }else{\n            $this->db->query(\"DELETE FROM `\" . DB_PREFIX . \"modification` WHERE `extension_install_id` = \'\" . (int)$extension_install_id . \"\'\");\n        }\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/controller/marketplace/install.php\">\n        <operation error=\"skip\">\n            <search><![CDATA[$this->model_setting_extension->deleteExtensionInstall($extension_install_id);]]></search>\n            <add position=\"replace\"><![CDATA[//d_opencart_patch.xml 1 moved down]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/controller/marketplace/install.php\">\n        <operation error=\"skip\">\n            <search><![CDATA[$this->model_setting_modification->deleteModificationsByExtensionInstallId($extension_install_id);]]></search>\n            <add position=\"after\"><![CDATA[\n                //d_opencart_patch.xml 2\n                $this->model_setting_extension->deleteExtensionInstall($extension_install_id);]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/controller/common/filemanager.php\">\n        <operation>\n            <search><![CDATA[$files = glob($directory . \'/\' . $filter_name . \'*.{jpg,jpeg,png,gif,JPG,JPEG,PNG,GIF}\', GLOB_BRACE);]]></search>\n            <add position=\"replace\"><![CDATA[$files = glob($directory . \'/\' . $filter_name . \'*.{jpg,jpeg,svg,png,gif,JPG,JPEG,PNG,GIF}\', GLOB_BRACE);]]></add>\n        </operation>\n        <operation>\n            <search><![CDATA[\'jpg\',]]></search>\n            <add position=\"after\"><![CDATA[\'svg\',]]></add>\n        </operation>\n        <operation>\n            <search><![CDATA[\'image/jpeg\',]]></search>\n            <add position=\"after\"><![CDATA[\'image/svg+xml\',]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/view/template/common/filemanager.twig\">\n        <operation>\n            <search><![CDATA[<img src=\"{{ image.thumb }}\" alt=\"{{ image.name }}\" title=\"{{ image.name }}\" />]]></search>\n            <add position=\"replace\"><![CDATA[<img src=\"{{ image.thumb }}\" alt=\"{{ image.name }}\" title=\"{{ image.name }}\" width=\"100\" height=\"100\" />]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/view/template/common/header.twig\">\n        <operation>\n            <search><![CDATA[</header>]]></search>\n            <add position=\"before\"><![CDATA[\n                <style type=\"text/css\">\n                    a.img-thumbnail{\n                        min-width:100px;\n                    }\n                </style>\n                ]]></add>\n        </operation>\n    </file>\n    <file path=\"admin/model/tool/image.php\">\n        <operation>\n            <search><![CDATA[public function resize($filename, $width, $height) {]]></search>\n            <add position=\"after\"><![CDATA[\n                if (isset($this->request->server[\'HTTPS\']) && (($this->request->server[\'HTTPS\'] == \'on\') || ($this->request->server[\'HTTPS\'] == \'1\'))) {\n                    $server = HTTPS_CATALOG;\n                } else {\n                    $server = HTTP_CATALOG;\n                }\n\n                if ($filename) {\n                    $image_info = @getimagesize(DIR_IMAGE . $filename);\n                    if (!$image_info) {\n                        return $server . \'image/\' . $filename;\n                    }\n                } else {\n                    $filename = \"no_image.png\";\n                }\n                ]]></add>\n        </operation>\n    </file>\n    <file path=\"catalog/model/tool/image.php\">\n        <operation>\n            <search><![CDATA[public function resize($filename, $width, $height) {]]></search>\n            <add position=\"after\"><![CDATA[\n                if (isset($this->request->server[\'HTTPS\']) && (($this->request->server[\'HTTPS\'] == \'on\') || ($this->request->server[\'HTTPS\'] == \'1\'))) {\n                    $server = HTTPS_SERVER;\n                } else {\n                    $server = HTTP_SERVER;\n                }\n\n                if ($filename) {\n                    $image_info = @getimagesize(DIR_IMAGE . $filename);\n                    if (!$image_info) {\n                        return $server . \'image/\' . $filename;\n                    }\n                } else {\n                    $filename = \"no_image.png\";\n                }\n                ]]></add>\n        </operation>\n    </file>\n</modification>', 1, '2020-04-11 04:51:44'),
(3, 0, 'd_twig_manager', 'd_twig_manager', 'Dreamvention', '2.0.0', 'http://dreamvention.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>d_twig_manager</name>\n    <code>d_twig_manager</code>\n    <description>Adds support for twig templating engine</description>\n    <version>2.0.0</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"\">\n            <search><![CDATA[$template = new Template($this->registry->get(\'config\')->get(\'template_engine\'));]]></search>\n            <add position=\"replace\"><![CDATA[\n            //d_twig_manager.xml\n            $template = new Template($this->registry->get(\'config\')->get(\'template_engine\'), $this->registry);\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/library/template.php\">\n        <operation error=\"skip\" info=\"\">\n            <search><![CDATA[public function __construct($adaptor) {]]></search>\n            <add position=\"replace\"><![CDATA[\n            //d_twig_manager.xml\n            public function __construct($adaptor, $registry) {\n            ]]></add>\n        </operation>\n        <operation error=\"skip\" info=\"\">\n            <search><![CDATA[$this->adaptor = new $class();]]></search>\n            <add position=\"replace\"><![CDATA[\n            //d_twig_manager.xml\n            $this->adaptor = new $class($registry);\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/library/template/twig.php\">\n        <operation error=\"skip\" info=\"\">\n            <search><![CDATA[private $data = array();]]></search>\n            <add position=\"after\"><![CDATA[\n            //d_twig_manager.xml\n            private $registry = array();\n            ]]></add>\n        </operation>\n        <operation error=\"skip\" info=\"\">\n            <search><![CDATA[public function __construct() {]]></search>\n            <add position=\"replace\"><![CDATA[\n            //d_twig_manager.xml\n            public function __construct($registry) {\n            ]]></add>\n        </operation>\n        <operation error=\"skip\" info=\"\">\n            <search><![CDATA[\\Twig_Autoloader::register();]]></search>\n            <add position=\"after\"><![CDATA[\n            //d_twig_manager.xml\n            $this->registry = $registry;\n            ]]></add>\n        </operation>\n        <operation error=\"skip\" info=\"\">\n            <search><![CDATA[$this->twig = new \\Twig_Environment($loader, $config);]]></search>\n            <add position=\"after\"><![CDATA[\n            //d_twig_manager.xml\n            if (file_exists(DIR_SYSTEM . \'library/template/Twig/Extension/DTwigManager.php\')) {\n                $this->twig->addExtension(new \\Twig_Extension_DTwigManager($this->registry));\n            }\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"catalog/controller/event/theme.php\">\n        <operation error=\"skip\" info=\"\">\n            <search><![CDATA[$twig = new \\Twig_Environment($loader, $config);]]></search>\n            <add position=\"after\"><![CDATA[\n            //d_twig_manager.xml\n            if (file_exists(DIR_SYSTEM . \'library/template/Twig/Extension/DTwigManager.php\')) {\n                $twig->addExtension(new Twig_Extension_DTwigManager($this->registry));\n            }\n            ]]></add>\n        </operation>\n    </file>\n</modification>', 1, '2020-04-09 09:37:16'),
(7, 5, 'Export/Import Tool (V3.22) for OpenCart 3.x', 'Export/Import Tool (V3.22) for OpenCart 3.x', 'mhccorp.com', '3.x-3.22', 'https://www.mhccorp.com', '<modification>\n	<name>Export/Import Tool (V3.22) for OpenCart 3.x</name>\n	<code>Export/Import Tool (V3.22) for OpenCart 3.x</code>\n	<version>3.x-3.22</version>\n	<author>mhccorp.com</author>\n	<link>https://www.mhccorp.com</link>\n	<file path=\"admin/controller/common/column_left.php\">\n		<operation>\n			<search><![CDATA[if ($this->user->hasPermission(\'access\', \'tool/upload\')) {]]></search>\n			<add position=\"before\"><![CDATA[\n			if ($this->user->hasPermission(\'access\', \'extension/export_import\')) {\n				$maintenance[] = array(\n					\'name\'	   => $this->language->get(\'text_export_import\'),\n					\'href\'     => $this->url->link(\'extension/export_import\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n					\'children\' => array()		\n				);\n			}\n			]]></add>\n		</operation>\n	</file>\n	<file path=\"admin/language/*/common/column_left.php\">\n		<operation>\n			<search><![CDATA[$_[\'text_backup\']]]></search>\n			<add position=\"after\"><![CDATA[\n$_[\'text_export_import\']             = \'Export / Import\';\n			]]></add>\n		</operation>\n	</file>\n</modification>\n', 1, '2020-04-18 01:52:59'),
(8, 6, 'Filter Products By Category', 'owdAdminProductsByCategoryFilter', 'Oleg Kononchuk', '1.2', 'http://buymeacoff.ee/D0daCsFcQ', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n    <name>Filter Products By Category</name>\r\n    <code>owdAdminProductsByCategoryFilter</code>\r\n    <version>1.2</version>\r\n    <author>Oleg Kononchuk</author>\r\n    <link>http://buymeacoff.ee/D0daCsFcQ</link>\r\n\r\n    <file path=\"admin/controller/catalog/product.php\">\r\n        <operation>\r\n            <search index=\"4\"><![CDATA[if (isset($this->request->get[\'filter_model\'])) {]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                if (isset($this->request->get[\'filter_category\'])) {\r\n                    $filter_category = $this->request->get[\'filter_category\'];\r\n                } else {\r\n                    $filter_category = \'\';\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[$url = \'\';]]></search>\r\n            <add position=\"after\"><![CDATA[\r\n            if (isset($this->request->get[\'filter_category\'])) {\r\n				$url .= \'&filter_category=\' . $this->request->get[\'filter_category\'];\r\n			}\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[$data[\'filter_model\'] = $filter_model;]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                $data[\'filter_category\'] = $filter_category;\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[\'filter_model\'	  => $filter_model,]]></search>\r\n            <add position=\"before\"><![CDATA[\'filter_category\'	  => $filter_category,]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[$data[\'sort\'] = $sort;]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                $data[\'text_category\'] = \"Category\";\r\n                $data[\'categories\'] = array();\r\n                $this->load->model(\'catalog/category\');\r\n                $cat_filter = array(\'sort\' => \'name\', \'order\'=> \'ASC\');\r\n                $results = $this->model_catalog_category->getCategories($cat_filter);\r\n                foreach ($results as $result) {\r\n                    $data[\'categories\'][] = array(\r\n                        \'category_id\' => $result[\'category_id\'],\r\n                        \'name\'        => $result[\'name\'],\r\n                    );\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n    <file path=\"admin/model/catalog/product.php\">\r\n        <operation>\r\n            <search><![CDATA[$sql = \"SELECT * FROM \" . DB_PREFIX . \"product p LEFT JOIN \" . DB_PREFIX . \"product_description pd ON (p.product_id = pd.product_id) WHERE pd.language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\";]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n                if (isset($data[\'filter_category\']) && $data[\'filter_category\'] !== \'\') {\r\n                    $sql = \"SELECT * FROM \" . DB_PREFIX . \"product p LEFT JOIN \" . DB_PREFIX . \"product_description pd ON (p.product_id = pd.product_id) LEFT JOIN \" . DB_PREFIX . \"product_to_category p2c ON (p.product_id = p2c.product_id) WHERE pd.language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\' AND p2c.category_id = \'\" . (int)$data[\'filter_category\'] . \"\'\";\r\n                }else{\r\n                    $sql = \"SELECT * FROM \" . DB_PREFIX . \"product p LEFT JOIN \" . DB_PREFIX . \"product_description pd ON (p.product_id = pd.product_id) WHERE pd.language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\";\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[$sql .= \" WHERE pd.language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\";]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n                if (isset($data[\'filter_category\']) && $data[\'filter_category\'] !== \'\') {\r\n                    $sql .= \" LEFT JOIN \" . DB_PREFIX . \"product_to_category p2c ON (p.product_id = p2c.product_id)\";\r\n                }\r\n                $sql .= \" WHERE pd.language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\";\r\n                if (isset($data[\'filter_category\']) && $data[\'filter_category\'] !== \'\') {\r\n                    $sql .= \" AND p2c.category_id = \'\" . $this->db->escape($data[\'filter_category\']) . \"\'\";\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n    <file path=\"admin/view/template/catalog/product_list.twig\">\r\n        <operation>\r\n            <search><![CDATA[<label class=\"control-label\" for=\"input-name\">{{ entry_name }}</label>]]></search>\r\n            <add position=\"before\" offset=\"1\">\r\n                <![CDATA[\r\n                <div class=\"form-group\">\r\n                    <label class=\"control-label\" for=\"filter-category\">{{ text_category }}&nbsp;<a href=\"https://www.buymeacoffee.com/D0daCsFcQ\" target=\"_blank\" data-toggle=\"tooltip\" title=\"Say thank you to the developer\"><i class=\"fa fa-coffee\"></i></a></label>\r\n                    <select name=\"filter_category\" id=\"filter-category\" class=\"form-control\">\r\n                    {% if categories %}\r\n                        <option value=\"\"></option>\r\n                        {% for category in categories %}\r\n                        <option value=\"{{ category.category_id }}\"{% if category.category_id == filter_category %} selected=\"selected\"{% endif %}>{{ category.name }}</option>\r\n                        {% endfor %}\r\n                    {% endif %}\r\n                </select>\r\n                </div>\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[var filter_model = $(\'input[name=\\\'filter_model\\\']\').val();]]></search>\r\n            <add position=\"before\" offset=\"1\">\r\n                <![CDATA[\r\n                var filter_category = $(\'select[name=\\\'filter_category\\\']\').val();\r\n\r\n                if (filter_category !== \'\') {\r\n                    url += \'&filter_category=\' + encodeURIComponent(filter_category);\r\n                }\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n    </file>\r\n\r\n</modification>', 1, '2020-04-18 01:57:41'),
(6, 4, 'smshare-core3', '56c6d', 'support@prowebtec.com − https://www.prowebtec.com', '2.0.0', 'https://www.prowebtec.com', '<modification>\n\n    <id><![CDATA[smshare-core3]]></id>\n\n    <version><![CDATA[2.0.0]]></version>\n\n    <author><![CDATA[support@prowebtec.com − https://www.prowebtec.com]]></author>\n\n    <code>56c6d</code>\n\n    <name>smshare-core3</name>\n\n    <link>https://www.prowebtec.com</link>\n\n    <file path=\"admin/controller/sale/order.php\">\n        <operation>\n            <search><![CDATA[\'sale/order_info\']]></search>\n            <add position=\"before\"><![CDATA[\n                $this->load->model(\'extension/module/smshare_core_nomod\');\n                $data[\'pws_notify_by_sms_checkbox\'] = $this->model_extension_module_smshare_core_nomod->get_my_checkbox($order_info, \'col-sm-2\', \'col-sm-10\');\n                $data[\'pws_notify_by_sms_js_param\'] = $this->model_extension_module_smshare_core_nomod->build_request_params_to_be_injected_in_the_url_of_the_order_status_update_ajax_request();\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search><![CDATA[protected function getList() {]]></search>\n            <add position=\"after\"><![CDATA[\n                $data[\'url_send_sms_from_order_list\'] = $this->url->link(\'extension/module/smshare_core/sendsms\', \'order_id=__ORDER_ID__&user_token=\' . $this->session->data[\'user_token\'], true);\n            ]]>\n            </add>\n        </operation>\n    </file>\n\n    <file path=\"admin/view/template/sale/order_info.twig\">\n        <operation>\n            <search><![CDATA[entry_notify]]></search>\n            <add position=\"before\" offset=\"1\"><![CDATA[\n            {{ pws_notify_by_sms_checkbox }}\n            ]]></add>\n        </operation>\n\n        <operation>\n            <search><![CDATA[+ \'&notify=\' +]]></search>\n            <add position=\"replace\"><![CDATA[+ {{ pws_notify_by_sms_js_param }} + \'&notify=\' +]]></add>\n        </operation>\n\n    </file>\n\n    <file error=\"skip\" path=\"catalog/controller/api/order.php\">\n        <!--\n        Pass smshare request parameter `notify_by_sms` to the addOrderHistory method\n        -->\n        <operation error=\"skip\">\n            <search><![CDATA[$this->model_checkout_order->addOrderHistory($order_id, $this->request->post[\'order_status_id\'], $this->request->post[\'comment\'], $this->request->post[\'notify\']]]>\n            </search>\n            <add position=\"replace\"><![CDATA[\n               $pws_notify_by_sms = isset($this->request->post[\'pws_notify_by_sms\']) ? $this->request->post[\'pws_notify_by_sms\'] : false ;\n               $this->model_checkout_order->addOrderHistory($order_id, $this->request->post[\'order_status_id\'], $this->request->post[\'comment\'], $this->request->post[\'notify\'], $pws_notify_by_sms]]></add><!-- do not indent -->\n        </operation>\n    </file>\n\n    <file path=\"catalog/model/checkout/order.php\">\n        <!--\n        add `notify_by_sms` to the function signature\n        -->\n        <operation error=\"skip\">\n            <search><![CDATA[public function addOrderHistory($order_id, $order_status_id, $comment = \'\', $notify = false]]></search>\n            <add position=\"replace\"><![CDATA[public function addOrderHistory($order_id, $order_status_id, $comment = \'\', $notify = false, $pws_notify_by_sms = false]]></add>\n        </operation>\n\n        <operation error=\"skip\">\n            <search><![CDATA[$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"order_history SET]]></search>\n            <add position=\"after\"><![CDATA[\n\n            // If order status is 0 then becomes greater than 0 send main html email\n			if (!$order_info[\'order_status_id\'] && $order_status_id) {\n				$args_d0530[\'order_info\']      = $order_info;\n                $args_d0530[\'order_status_id\'] = $order_status_id;\n\n                $this->load->model(\'extension/module/smshare_core\');\n                $this->model_extension_module_smshare_core->notify_about_checkout($args_d0530);\n			}\n\n			// If order status is not 0 then send update text email\n			if ($order_info[\'order_status_id\'] && $order_status_id && $pws_notify_by_sms) {\n\n                //rc send sms\n                $args_d0530[\'order_info\']      = $order_info;\n                $args_d0530[\'comment\']         = $comment;\n                $args_d0530[\'order_status_id\'] = $order_status_id;\n                $args_d0530[\'powertrack_info\'] = isset($powertrack_info) ? $powertrack_info : \'\';\n\n                $this->load->model(\'extension/module/smshare_core\');\n                $this->model_extension_module_smshare_core->notify_about_order_status_update($args_d0530);\n			}\n\n            ]]>\n            </add>\n        </operation>\n\n    </file>\n    \n\n    \n\n    \n\n    \n\n    \n\n    \n\n    <file path=\"admin/view/template/sale/order_list.twig\">\n		<operation>\n            <search><![CDATA[id=\"button-shipping\"]]></search>\n            <add position=\"before\"><![CDATA[\n            <button data-tv-button-send-sms data-tv-url={{ url_send_sms_from_order_list }} data-toggle=\"tooltip\" title=\"Send SMS to selected\" class=\"btn btn-info ladda-button\" data-style=\"zoom-in\"><i class=\"fa fa-paper-plane\"></i></button>\n            ]]>\n            </add>\n        </operation>\n	</file>\n\n    <file path=\"admin/controller/sale/order.php\">\n        \n        <!-- =========\n          Inject ladda\n        ========== -->\n        <operation>\n            <search><![CDATA[protected function getList() {]]></search>\n            <add position=\"after\"><![CDATA[\n            //smshare ladda\n            $this->document->addScript(\'view/javascript/smshare/lib/ladda/spin.min.js\');\n            $this->document->addScript(\'view/javascript/smshare/lib/ladda/ladda.min.js\');\n            $this->document->addStyle(\'view/javascript/smshare/lib/ladda/ladda-themeless.min.css\');\n\n            //sweetalert\n            $this->document->addStyle(\'view/javascript/smshare/lib/sweetalert2/dist/sweetalert2.css\');\n            $this->document->addScript(\'view/javascript/smshare/lib/sweetalert2/dist/sweetalert2.all.min.js\');\n\n            $this->document->addScript(\'view/javascript/smshare/smshare-send-sms-from-order-list.js\');\n\n            ]]></add>\n        </operation>\n        \n    </file>\n\n\n</modification>', 1, '2020-04-11 23:13:06'),
(9, 7, '[underr] Total Order Discount', 'total-order-discount', 'Andrii Burkatskyi aka underr', '1.4', 'https://github.com/underr-ua/ocmod3-total-order-discount', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n\n<!--\nTotal Order Discount.\n\nThe Total Order Discount extension is for CMS Opencart 3.x.\nIt allows to give a discount based on purchase total price or quantity of products in the cart.\n\n@author      Andrii Burkatskyi aka underr underr.ua@gmail.com\n@copyright   Copyright (c) 2018-2019 Andrii Burkatskyi\n@license     https://raw.githubusercontent.com/underr-ua/ocmod3-total-order-discount/master/LICENSE.txt MIT\n\n@version     1.4\n\n@see         https://www.opencart.com/index.php?route=marketplace/extension/info&extension_id=33296\n@see         https://underr.space/notes/projects/project-001.html\n@see         https://github.com/underr-ua/ocmod3-total-order-discount\n-->\n\n<modification>\n	<name>[underr] Total Order Discount</name>\n	<code>total-order-discount</code>\n	<version>1.4</version>\n	<author>Andrii Burkatskyi aka underr</author>\n	<link>https://github.com/underr-ua/ocmod3-total-order-discount</link>\n\n  <!-- Sub-Total sort order issue fix -->\n  <file error=\"log\" path=\"admin/controller/extension/total/sub_total.php|admin/view/template/extension/total/sub_total.twig\">\n    <operation error=\"log\">\n      <search><![CDATA[sub_total_sort_order]]></search>\n      <add position=\"replace\"><![CDATA[total_sub_total_sort_order]]></add>\n    </operation>\n  </file>\n\n\n</modification>\n', 1, '2020-04-18 06:38:19');
INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES
(10, 8, 'Customer Membership', 'customer-membership', 'ModulePoints', '1.0', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<modification>\r\n	<name>Customer Membership</name>\r\n	<version>1.0</version>\r\n	<code>customer-membership</code>\r\n  <author>ModulePoints</author>\r\n	<file path=\"admin/controller/common/column_left.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[// Extension]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			/* mps */\r\n			$mpmembership = array();\r\n			$this->load->language(\'modulepoints/menu_membership\');\r\n			if ($this->user->hasPermission(\'access\', \'modulepoints/mpplan\')) {\r\n				$mpmembership[] = array(\r\n					\'name\'	   => $this->language->get(\'menu_plan\'),\r\n					\'href\'     => $this->url->link(\'modulepoints/mpplan\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($this->user->hasPermission(\'access\', \'modulepoints/plansetting\')) {\r\n				$mpmembership[] = array(\r\n					\'name\'	   => $this->language->get(\'menu_plansetting\'),\r\n					\'href\'     => $this->url->link(\'modulepoints/plansetting\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($mpmembership) {\r\n				$data[\'menus\'][] = array(\r\n					\'id\'       => \'menu-membership\',\r\n					\'icon\'	   => \'fa-user\',\r\n					\'name\'	   => $this->language->get(\'menu_membership\'),\r\n					\'href\'     => \'\',\r\n					\'children\' => $mpmembership\r\n				);\r\n			}\r\n			/* mpe */\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/common/menu.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[return $this->load->view(\'common/menu\', $data);]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n		if($data[\'categories\'] && $this->config->get(\'mpplan_status\')) {\r\n			$this->load->language(\'membership/information\');\r\n			$data[\'categories\'][] = array(\r\n				\'name\'     => $this->language->get(\'menu_membership\'),\r\n				\'children\' => array(),\r\n				\'column\'   => 1,\r\n				\'href\'     => $this->url->link(\'membership/prices\', \'\', true),\r\n			);\r\n		}\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/account/account.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[public function index() {]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n		$this->load->language(\'membership/information\');\r\n		$data[\'menu_membership_account\'] = $this->language->get(\'menu_membership_account\');\r\n		$data[\'membership_account\'] = $this->url->link(\'account/subscriptions\', \'\', true);\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/view/theme/*/template/account/account.twig\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[<li><a href=\"{{ wishlist }}\">{{ text_wishlist }}</a></li>]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				<li><a href=\"{{ membership_account }}\">{{ menu_membership_account }}</a></li>\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n\r\n	<file path=\"admin/model/sale/order.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[public function deleteOrder($order_id) {]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n		/* MP Membership Starts */\r\n		$this->db->query(\"DELETE FROM `\" . DB_PREFIX . \"mpplan_customer` WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n		/* MP Membership Ends */\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/checkout/confirm.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$order_data[\'products\'][] = array(]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n					/* MP Membership Starts */\r\n					\'mpplan_details\'    => isset($product[\'mpplan_details\']) ? $product[\'mpplan_details\'] : array(),\r\n					/* MP Membership Ends */\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/model/checkout/order.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$order_product_id = $this->db->getLastId();]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				/* MP Membership Starts */\r\n				if(!empty($product[\'mpplan_details\']) && $this->config->get(\'mpplan_status\')) {\r\n					$this->db->query(\"INSERT INTO `\" . DB_PREFIX . \"mpplan_customer` SET `order_id` = \'\" . (int)$order_id . \"\', `order_product_id` = \'\" . (int)$order_product_id . \"\', `customer_id` = \'\" . (int)$data[\'customer_id\'] . \"\', `mpplan_id` = \'\" . (int)$product[\'mpplan_details\'][\'mpplan_id\'] . \"\', `plan_name` = \'\" . $this->db->escape($product[\'mpplan_details\'][\'plan_name\']) . \"\', `start_date` = \'\" . $this->db->escape($product[\'mpplan_details\'][\'start_date\']) . \"\', `end_date` = \'\" . $this->db->escape($product[\'mpplan_details\'][\'end_date\']) . \"\', `duration_type` = \'\" . $this->db->escape($product[\'mpplan_details\'][\'duration_type\']) . \"\', `duration_value` = \'\" . $this->db->escape($product[\'mpplan_details\'][\'duration_value\']) . \"\', `plan_info` = \'\" . $this->db->escape($product[\'mpplan_details\'][\'plan_info\']) . \"\'\");\r\n				}\r\n				/* MP Membership Ends */\r\n			]]></add>\r\n		</operation>\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$this->db->query(\"DELETE FROM \" . DB_PREFIX . \"order_product WHERE order_id = \'\" . (int)$order_id . \"\'\");]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n		/* MP Membership Starts */\r\n		if($this->config->get(\'mpplan_status\')) {\r\n			$this->db->query(\"DELETE FROM `\" . DB_PREFIX . \"mpplan_customer` WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n		}\r\n		/* MP Membership Ends */\r\n			]]></add>\r\n		</operation>\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[addOrderHistory($order_id, $order_status_id, $comment = \'\', $notify = false, $override = false) {]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n		/* MP Membership Starts */\r\n		if($this->config->get(\'mpplan_status\')) {\r\n		if($order_status_id == $this->config->get(\'mpplan_status_id\')) {\r\n			$mpplan_query = $this->db->query(\"SELECT * FROM \". DB_PREFIX .\"mpplan_customer WHERE order_id = \'\". (int)$order_id .\"\' ORDER BY mpplan_customer_id DESC LIMIT 0,1\");\r\n			if($mpplan_query->row) {\r\n				$this->db->query(\"UPDATE \". DB_PREFIX .\"mpplan_customer SET active = \'0\' WHERE customer_id = \'\". (int)$mpplan_query->row[\'customer_id\'] .\"\'\");\r\n\r\n				$this->db->query(\"UPDATE \". DB_PREFIX .\"mpplan_customer SET active = \'1\' WHERE mpplan_customer_id = \'\". (int)$mpplan_query->row[\'mpplan_customer_id\'] .\"\' AND customer_id = \'\". (int)$mpplan_query->row[\'customer_id\'] .\"\'\");\r\n\r\n			}\r\n		}\r\n		}\r\n		/* MP Membership Ends */\r\n			]]></add>\r\n		</operation>\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[public function deleteOrder($order_id) {]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n		/* MP Membership Starts */\r\n		$this->db->query(\"DELETE FROM `\" . DB_PREFIX . \"mpplan_customer` WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n		/* MP Membership Ends */\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/product/product.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$data[\'heading_title\'] = $product_info[\'name\'];]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			/* MP Membership Starts */\r\n			if(isset($this->request->get[\'product_id\']) && $this->config->get(\'mpplan_status\') && $this->config->get(\'mpplan_product_id\') == $this->request->get[\'product_id\']) {\r\n				$this->response->redirect($this->url->link(\'membership/prices\', \'\', true));\r\n			}\r\n\r\n			if($this->config->get(\'mpplan_status\') && $this->config->get(\'mpplan_product_status\')) {\r\n				$this->load->language(\'membership/information\');\r\n				$this->load->model(\'membership/prices\');\r\n\r\n				$mpplan_description = (array)$this->config->get(\'mpplan_description\');\r\n				$data[\'membership_buttontext\'] = !empty($mpplan_description[$this->config->get(\'config_language_id\')][\'buttontext\']) ? $mpplan_description[$this->config->get(\'config_language_id\')][\'buttontext\'] : $this->language->get(\'membership_buttontext\');\r\n\r\n				$data[\'membership_modaltext\'] = $this->language->get(\'membership_modaltext\');\r\n				$data[\'column_package\'] = $this->language->get(\'column_package\');\r\n				$data[\'column_regular\'] = $this->language->get(\'column_regular\');\r\n				$data[\'column_membership\'] = $this->language->get(\'column_membership\');\r\n				$data[\'column_yousave\'] = $this->language->get(\'column_yousave\');\r\n\r\n				$data[\'membership_prices\'] = array();\r\n				if($this->customer->isLogged()) {\r\n					$customer_group_id = $this->customer->getGroupId();\r\n				} else {\r\n					$customer_group_id = $this->config->get(\'config_customer_group_id\');\r\n				}\r\n\r\n				$membership_prices = $this->model_membership_prices->getProductPlans($product_info[\'product_id\']);\r\n				foreach ($membership_prices as $membership_price) {\r\n					$mpplan_info = $this->model_membership_prices->getMpplan($membership_price[\'mpplan_id\']);\r\n					if($mpplan_info) {\r\n						$plan_discount = $this->model_membership_prices->getMpplanDiscount($mpplan_info[\'mpplan_id\'], $customer_group_id);\r\n\r\n						$regular_price = false;\r\n						if ($this->customer->isLogged() || !$this->config->get(\'config_customer_price\')) {\r\n							$regular_price = $this->tax->calculate($product_info[\'price\'], $product_info[\'tax_class_id\'], $this->config->get(\'config_tax\'));\r\n						}\r\n						if ((float)$product_info[\'special\']) {\r\n							$regular_price = $this->tax->calculate($product_info[\'special\'], $product_info[\'tax_class_id\'], $this->config->get(\'config_tax\'));\r\n						}\r\n\r\n						if($regular_price && $plan_discount[\'discount\']) {\r\n							$membership_price = $regular_price - (($regular_price * $plan_discount[\'discount\']) / 100);\r\n						} else {\r\n							$membership_price = $regular_price;\r\n						}\r\n\r\n						$save_price = $regular_price - $membership_price;\r\n\r\n						$data[\'membership_prices\'][] = array(\r\n							\'plan_name\'			=> $mpplan_info[\'name\'],\r\n							\'price\'       		=> $this->currency->format($regular_price, $this->session->data[\'currency\']),\r\n							\'membership_price\' 	=> $this->currency->format($membership_price, $this->session->data[\'currency\']),\r\n							\'save_price\'  		=> $this->currency->format($save_price, $this->session->data[\'currency\']),\r\n						);\r\n					}\r\n				}\r\n			} else {\r\n				$data[\'membership_buttontext\'] = \'\';\r\n				$data[\'membership_prices\'] = array();\r\n			}\r\n\r\n			/* MP Membership Ends */\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/view/theme/*/template/product/product.twig\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[{{ button_cart }}</button>]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			  <!-- /* MP Membership Starts */ -->\r\n              {% if membership_buttontext and membership_prices %}\r\n              <button type=\"button\" class=\"btn btn-success btn-block btn-lg\" data-toggle=\"modal\" data-target=\"#membership-modal\">{{ membership_buttontext }}</button>\r\n              {% endif %}\r\n              <!-- /* MP Membership Ends */ -->\r\n			]]></add>\r\n		</operation>\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[{{ footer }}]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				<!-- /* MP Membership Starts */ -->\r\n				{% if membership_buttontext and membership_prices %}\r\n				<div id=\"membership-modal\" class=\"modal fade\" role=\"dialog\">\r\n				  <div class=\"modal-dialog\">\r\n				    <div class=\"modal-content\">\r\n				      <div class=\"modal-header\">\r\n				        <button type=\"button\" class=\"close\" data-dismiss=\"modal\">&time